import { PUT_AUTH_DATA, PUT_FAILED_AUTH_DATA } from './actions';

const initialState = {
  token: '',
  success: false,
}

export const authReducer = (state = initialState, action) => {
  console.log(action.type, action.payload);
  switch (action.type) {
    
    case PUT_AUTH_DATA: return {
      ...state,
      token: action.payload.data ? action.payload.data.token : state.payload.access_token || '',
      success: true,
    }

    case PUT_FAILED_AUTH_DATA: return {
      ...state,
      success: false,
      error: 'Ошибка сервера'
    }

    default: return state;  
  }
}
