import React, {Component} from 'react';
import $ from 'jquery';

class SidebarItem extends Component {
  changeParentItem() {
    $(this.parentLi).toggleClass('left-menu__link_opened');
		$(this.parentLi).parent().toggleClass('left-menu__item_opened');
		$(this.parentLi).siblings('.left-menu__second-list').stop(true, false).slideToggle();
  }

  render() {
    return (
      <li className="left-menu__item" ref={li => this.parentLi = li} onClick={() => this.changeParentItem()}>
        <a className="left-menu__link" href={this.props.item.link}>{this.props.item.text}</a>
          <ul className="left-menu__second-list">
          {this.props.childs.map(child => {
            return(
              <li className="left-menu__second-list-item" key={child.id}>
                <a className="left-menu__second-list-link" href={child.link}>{child.text}</a>
              </li>
            );
          })}
        </ul>
      </li>
    );
  }
}
export default SidebarItem;
