import { takeEvery, put, call } from 'redux-saga/effects';
import { 
	SEND_REGISTER_DATA, 
	GET_USER_DATA,
	SEND_LOGIN_DATA, 
	putAuthData, 
	putUserData,
	putFailedAuthData 
} from './actions';

import * as config from '../config';
import fetchWithAuth from '../fetchWithAuth';

// Workers
function* workerRegisterData(action) {
	
	const data = yield fetchWithAuth(config.URL_REGISTER_API, 'POST', action.payload);

	if (data && data.success === true) 	
		yield put(putAuthData(data));
	else 
		yield put(putFailedAuthData(data));
}

function* workerLoginData(action) {
	const newData = {
		username: action.payload.email,
		password: action.payload.password,
		grant_type: 'password',
		client_id: config.CLIENT_ID,
		client_secret: config.CLIENT_SECRET
	}

	const data = yield fetchWithAuth(config.URL_LOGIN_API, 'POST', newData);
	
	if (data) 	
		yield put(putAuthData(data));
	else 
		yield put(putFailedAuthData(data));
}

function* workerGetUserData(action) {
	
	const data = yield fetchWithAuth(config.URL_USER_API);
	
	console.log(data && data.sucess === true);

	if (data && data.success === true) 	
		yield put(putAuthData(data));
	else 
		yield put(putFailedAuthData(data));
}


// Wathcers
export function* watchRegisterData() {
	yield takeEvery(SEND_REGISTER_DATA, workerRegisterData);
}

export function* watchLoginData() {
	yield takeEvery(SEND_LOGIN_DATA, workerLoginData);
}

export function* watchGetUserData() {
	yield takeEvery(GET_USER_DATA, workerGetUserData);
}
