import React, {Component} from 'react';
import Tag from '../UI/Tag/Tag.js'
import './SnippetNav.sass';


let snippets = [
  {type: 'html', id: '1', id: '1'},
  {type: 'js', id: '2', id: '2'}
];

class SnippetNav extends Component {
  constructor(props) {
    super(props); 

    this.state = {
      snippets, 
      newSnippetType: ''
    }
  }

  addSnippet(e) {
    e.preventDefault();

    if (!this.state.newSnippetType.length > 1)
      return false;

    this.setState({
      snippets: this.state.snippets.concat({
        type: this.state.newSnippetType, 
        id: this.state.newSnippetType + Number(new Date()),
      }),
      newSnippetType: ''
    });

  }

  setNewSnippetType(e) {
    this.setState({ newSnippetType: e.target.value });
  }

  removeSnippet(e) {
    console.log(e.target.id, 'будет удален');
  }  

  render() {
    return(
      <nav className="top-content-nav">
        <ul className="top-list">
          {this.state.snippets.map(snippet => (<li className="top-list__item"><a className="top-list__link active" href="#">{snippet.type}</a></li>))}
          
          <li className="top-list__item">
            <a href="block:#add-snippet" className="top-list__button">+</a>

            <div id="add-snippet" className="top-list__add-block mini-block">
              <div className="mini-block__title">Добавить сниппет</div>
              
              <div className="tags">
                {this.state.snippets.map(snippet => (<Tag onClick={(e) => this.removeSnippet(e)} id={snippet.id} text={snippet.type}/>))} 
              </div>

              <form className="top-list__form" onSubmit={(e) => this.addSnippet(e)}>
                <input onChange={(e) => this.setNewSnippetType(e)} value={this.state.newSnippetType} className="top-list__form-input bottom-line-input" type="text" placeholder="Тип" />

                <div className="top-list__form-buttons">
                  <button className="btn-grey btnclr close-block top-list__form-button">Отмена</button>
                  <button type="submit" className="btn-grey btnclr top-list__form-button">Добавить</button>		
                </div>						
              </form>

            </div>
          </li>
        </ul>
      
      </nav>

    );
  }
}
export default SnippetNav;
