export const SEND_AUTH_DATA = 'SEND_AUTH_DATA';
export const SEND_LOGIN_DATA = 'SEND_LOGIN_DATA';
export const PUT_AUTH_DATA = 'PUT_AUTH_DATA';
export const PUT_FAILED_AUTH_DATA = 'PUT_FAILED_ATUH_DATA';


// Login
export const sendLoginData = (data) => {
  return {
    type: SEND_LOGIN_DATA,
    payload: data
  };
}

export const sendRegisterData = (data) => {
  return {
    type: SEND_REGISTER_DATA,
    payload: data
  }
}

export const putAuthData = (data) => {
  return {
    type: PUT_AUTH_DATA,
    payload: data
  };
}

export const putFailedAuthData = (data) => {
  return {
    type: PUT_FAILED_AUTH_DATA,
    payload: data
  };
}
