import React from 'react';
import {useParams} from 'react-router-dom';
import {useEffect, useState} from 'react';
import Loader from '../UI/Loader/Loader';
import TopList from '../TopList/TopList';
import CreateSnippetForm from './CreateSnippetForm';

const Snippets = (props) => {
  
  let { componentId, projectId, snippetId } = useParams();
  let [lastComponentId, setLastComponentId] = useState(componentId);

  useEffect(() => {
  
    if (props.snippets.snippetsList.data === undefined || componentId != lastComponentId) 
      props.getSnippets(componentId);

    setLastComponentId(componentId);
  });

  function openCreateComponentForm() {
    props.openPopup(
      <CreateSnippetForm 
        closePopup={props.closePopup}
        componentId={componentId}
        addNotify={props.addNotify}
        createSnippet={props.createSnippet}
      />
    );
  }

  return (
    <div>
      {(props.snippets.snippetsList.data && componentId == lastComponentId) && <TopList linkGen={`/project${projectId}/component{component_id}/snippet{id}`} onMenuButtonClick={openCreateComponentForm} items={props.snippets.snippetsList.data} />}
      {(!props.snippets.snippetsList.data || componentId != lastComponentId) && <Loader />} 
    </div>
  );
};

export default Snippets;
