import { PUT_AUTH_DATA, PUT_FAILED_REGISTER_DATA, PUT_FAILED_LOGIN_DATA} from './actions';

const initialState = {
  token: '',
  success: false,
  message: false,
  userName: 'user'
}

export const authReducer = (state = initialState, action) => {
  console.log(action.type, action.payload);
  switch (action.type) {
    
    case PUT_AUTH_DATA: return {
      ...state,
      ...action.payload
    }

    case PUT_FAILED_REGISTER_DATA: return {
      ...state, 
      success: action.payload.success,
      message: action.payload.message,
      errors: action.payload.data, 
      userName: 'user'
    }

    case PUT_FAILED_LOGIN_DATA: return {
      ...state, 
      token: '',
      message: '', 
      userName: 'user'
    }    

    default: return state;  
  }
}
