import React, { useState } from 'react';

import { MiniBlock, MiniBlockTitle, MiniBlockInput, MiniBlockForm } from '../UI/MiniBlock/MiniBlock';
import './Auth.sass';
import SimpleButton from '../UI/SimpleButton/SimpleButton';

import logo from '../../img/logo.png';



export default function Register(props) {
  const [fields, setField] = useState({ 
    data: {
      login: '',
      email: '',
      password: '',
      c_password: ''
    }
    
  });

  function handleInputChange(event) {
    const target = event.target;
    const value = target.name === 'isGoing' ? target.checked : target.value;
    const name = target.name;
  
    setField({
      
      data: { ...fields.data, [name]: value }
    });
  }

  function registerOnSubmit(e) {
    e.preventDefault();
    props.sendRegisterData(fields.data);
  }


  return (
    <div className="auth-screen">
      
      <div className="auth-screen__logo">
        <img src={logo} alt=""/>
      </div>

      <MiniBlock className="auth-screen__form">
        <MiniBlockTitle>Регистрация</MiniBlockTitle>
        

        <MiniBlockForm onSubmit={ (e) => { registerOnSubmit(e) } }>
          <MiniBlockInput name="name" onChange={(e) => { handleInputChange(e) }} placeholder="Логин" />
          <MiniBlockInput name="email" onChange={(e) => { handleInputChange(e) }} placeholder="Email" />
          <MiniBlockInput name="password" onChange={(e) => { handleInputChange(e) }} type="password" placeholder="Пароль" />
          <MiniBlockInput name="c_password" onChange={(e) => { handleInputChange(e) }} type="password" placeholder="Повторите пароль" />

          <SimpleButton type="submit" className="auth-screen__form-button" width="wide">Зарегистрироваться</SimpleButton>
        </MiniBlockForm>
        
      </MiniBlock>
    </div>
  );
}
