import { PUT_REGISTER_DATA, PUT_FAILED_REGISTER_DATA } from './actions';

const initialState = {
  data: {},
  success: false,
  message: false,
  errors: {}
}

export const authReducer = (state = initialState, action) => {
  switch (action.type) {
    
    case PUT_REGISTER_DATA: return { 
      ...state, 
      success: action.payload.success,
      token: action.payload.data.token,
      userName: action.payload.data.name
    }; 

    case PUT_FAILED_REGISTER_DATA: return {
      ...state, 
      errors: action.payload.data
    }

    

    default: return state;

    
  }
}
