import React, { Component } from 'react';
import './Sidebar.sass';
import './_sidebar.js';
import SidebarItem from './SidebarItem.js';
import $ from 'jquery';
import { MiniBlock, MiniBlockTitle, MiniBlockInput, MiniBlockForm } from '../UI/MiniBlock/MiniBlock';



class Sidebar extends Component {

  constructor(props) {
    super(props);
    
    this.state = {
      isHoverd: false
    }
  }

  setHover() { this.setState({ isHoverd: true }) }

  unsetHover() { this.setState({ isHoverd: false }) }


  render(props) {
    let sbClassName = 'sidebar'; 
    let sbButtonClassName = 'sidebar__button';

    if (!this.props.isOpen)
      sbClassName += ' hidden';

    if (this.state.isHoverd) {
      sbClassName += ' hover';
      sbButtonClassName += ' hover';
    }
      
    

    


      
    return (
      <aside className={sbClassName}>
        
        <div className="sidebar__hover-line" 
          onMouseEnter={this.setHover.bind(this)} 
          onMouseLeave={this.unsetHover.bind(this)}
          onClick={this.props.toggleSidebar}
          ></div>
        
        <div className="sidebar__head">
          <span className="sidebar__pre-title">
            Project:
          </span>
          <h1 className="sidebar__title">Future project</h1>
        </div>

        <nav className="left-menu">

          <ul className="left-menu__list">
            {this.props.menuItems.map(item => {
              const childs = this.propsmenuItems.filter(_item => item.id == _item.parentId );
              if (item.parentId == 0)
                return (
                  <SidebarItem item={item} childs={childs} key={item.id}/>
                );
            })}
          </ul>

        </nav>

        <div 
          className={sbButtonClassName}
          onClick={this.props.toggleSidebar}
          onMouseEnter={this.setHover.bind(this)} 
          onMouseLeave={this.unsetHover.bind(this)}>
          <svg className="sidebar__button-icon" xmlns="http://www.w3.org/2000/svg" width="10.567" height="16.95" viewBox="0 0 10.567 16.95">
            <path id="Path_3" data-name="Path 3" d="M314.383,530.331l-8.4,8.058,8.4,7.422" transform="translate(315.076 546.56) rotate(180)" fill="none" stroke="#918f8f" stroke-linecap="round" stroke-linejoin="round" stroke-width="2"/>
          </svg>
          
        </div>

          <div className="sidebar__bottom">
            <a href="block:#add-component" className="btnclr button-add sidebar__add">
              Добавить компонент<i className="button-add__icon fas fa-plus"></i>
              
            
            </a>
          
            <MiniBlock className="slide-from-bottom sidebar__add-form" id="add-component"> 
              <MiniBlockTitle> Добавить компонент</MiniBlockTitle>

              <MiniBlockForm>
                <MiniBlockInput onChange={function() {}} type="text" placeholder="Название"/>
                <MiniBlockInput onChange={function() {}} type="select" placeholder="Название"/>
              </MiniBlockForm>
            </MiniBlock>

          </div>

        </aside>
    );

  }
}


export default Sidebar;
