import React, { Component, Fragment } from 'react'; 
import ReactDOM from 'react-dom';
import { CSSTransitionGroup } from 'react-transition-group';

import Notify from './Notify';

import './NotifyList.sass';

export default class NotifyList extends Component {
  render() {
    return ReactDOM.createPortal(
      
      <div onClick={this.props.onClick} className="modal-notify-wrap">
        <CSSTransitionGroup transitionName="notify" transitionEnterTimeout={300} transitionLeaveTimeout={300}>
          {this.props.notify.map((item, key) => <Notify key={item}>{item.text}</Notify>)}
        </CSSTransitionGroup>
      </div>,
      document.getElementById('modals')
    );
  }
}
