export const GET_SNIPPETS = 'GET_SNIPPETS';

export const PUT_SNIPPETS = 'PUT_SNIPPETS';


// Requests 

export const getSnippets = (componentId) => {
  return {
    type: GET_SNIPPETS,
    payload: componentId
  }
}

// Put

export const putGottenSnippets = (response) => {
  return { type: PUT_SNIPPETS, payload: response }
}
